package com.study.repository.high.concurrence;import com.study.repository.high.concurrence.filter.HttpFilter;import com.study.repository.high.concurrence.interceptor.HttpInterceptor;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;/** * @author * @description * @date 2020/1/13 */@SpringBootApplicationpublic class HighConcurrenceApplication extends WebMvcConfigurerAdapter {    public static void main(String[] args) {        SpringApplication.run(HighConcurrenceApplication.class, args);    }    @Bean    public FilterRegistrationBean filterRegistrationBean() {        FilterRegistrationBean filterRegistrationBean = new FilterRegistrationBean();        filterRegistrationBean.setFilter(new HttpFilter());        filterRegistrationBean.addUrlPatterns("/test/*");        return filterRegistrationBean;    }    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(new HttpInterceptor())                .addPathPatterns("/**");    }}