package com.study.repository.high.concurrence.aqs;import lombok.extern.slf4j.Slf4j;import java.util.concurrent.Callable;import java.util.concurrent.ExecutionException;import java.util.concurrent.FutureTask;/** * @author * @description * @date 2020/1/16 */@Slf4jpublic class FutureTaskTest {    public static void main(String[] args) throws ExecutionException, InterruptedException {        FutureTask<String> futureTask = new FutureTask<>(new Callable<String>() {            @Override            public String call() throws Exception {                log.info("开始处理");                Thread.sleep(2000);                return "成功";            }        });        futureTask.run();        log.info("等待线程处理结果");        String result = futureTask.get();        log.info("result {}", result);    }}