package com.study.repository.high.concurrence.filter;import com.study.repository.high.concurrence.threadLocal.HttpThreadLocal;import lombok.extern.slf4j.Slf4j;import javax.servlet.*;import javax.servlet.http.HttpServletRequest;import java.io.IOException;/** * @author * @description * @date 2020/1/15 */@Slf4jpublic class HttpFilter implements Filter {    @Override    public void init(FilterConfig filterConfig) throws ServletException {    }    @Override    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain) throws IOException, ServletException {        HttpServletRequest httpServletRequest = (HttpServletRequest) servletRequest;        log.info("{} - {}", Thread.currentThread().getId(), httpServletRequest.getMethod());        HttpThreadLocal.add(Thread.currentThread().getId());        filterChain.doFilter(httpServletRequest, servletResponse);    }    @Override    public void destroy() {    }}