package com.study.repository.high.concurrence.interceptor;import com.study.repository.high.concurrence.threadLocal.HttpThreadLocal;import lombok.extern.slf4j.Slf4j;import org.springframework.web.servlet.handler.HandlerInterceptorAdapter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * @author * @description * @date 2020/1/15 */@Slf4jpublic class HttpInterceptor extends HandlerInterceptorAdapter {    @Override    public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {        log.info("{} - preHandle", HttpThreadLocal.get());        return true;    }    @Override    public void afterCompletion(HttpServletRequest request, HttpServletResponse response, Object handler, Exception ex) throws Exception {        log.info("afterCompletion");    }}