package com.study.repository.high.concurrence.threadLocal;/** * @author * @description * @date 2020/1/15 */public class HttpThreadLocal {    private final static ThreadLocal<Long> threadLocal = new ThreadLocal<>();    public static void add(long id) {        threadLocal.set(id);    }    public static long get() {        return threadLocal.get();    }    public static void remove() {        threadLocal.remove();    }}